// Layout
// -----------------------------------------------------------------------------
//
// Define the layout width's for your project.
//
// -----------------------------------------------------------------------------

// Max Width
$layout-widthMax:               remCalc(1200px) !default;

// Fixed Width
$layout-widthFixed:             90% !default;


// Layout Map
// -----------------------------------------------------------------------------

$layoutMap: (
    max:                        $layout-widthMax,
    fixed:                      $layout-widthFixed
);


// Spacing Function
// -----------------------------------------------------------------------------

@function layout($key) {

    @if map-has-key($layoutMap, $key) {
        @return map-get($layoutMap, $key);
    }

    @warn "Unknown `#{$key}` in $layoutMap.";
    @return null;
}


// Spacing Rhythm
// -----------------------------------------------------------------------------
//
// Create consistent spacing patterns in your entire projects aesthetics.
// Build your components margins, paddings, etc based off these spacing vars.
//
// -----------------------------------------------------------------------------

// Base Spacing
$spacingRhythm:                 $fontSize-base * $lineHeight-base !default;

$spacing-base:                  1rem !default;
$spacing-single:                $spacingRhythm !default;
$spacing-double:                $spacingRhythm * 2 !default;
$spacing-half:                  $spacingRhythm / 2 !default;
$spacing-third:                 $spacingRhythm / 3 !default;
$spacing-quarter:               $spacingRhythm / 4 !default;
$spacing-fifth:                 $spacingRhythm / 5 !default;
$spacing-sixth:                 $spacingRhythm / 6 !default;
$spacing-eighth:                $spacingRhythm / 8 !default;


// Spacing Map
// -----------------------------------------------------------------------------

$spacingMap: (
    base:                       $spacing-base,
    single:                     $spacing-single,
    double:                     $spacing-double,
    half:                       $spacing-half,
    third:                      $spacing-third,
    quarter:                    $spacing-quarter,
    fifth:                      $spacing-fifth,
    sixth:                      $spacing-sixth,
    eighth:                     $spacing-eighth
);


// Spacing Function
// -----------------------------------------------------------------------------

@function spacing($key) {

    @if map-has-key($spacingMap, $key) {
        @return map-get($spacingMap, $key);
    }

    @warn "Unknown `#{$key}` in $spacingMap.";
    @return null;
}

// =============================================================================
// CONTAINERS
// =============================================================================
//
// Purpose:
// The centralised place to control container settings like borders and fill.
//
// -----------------------------------------------------------------------------


// Borders
// -----------------------------------------------------------------------------

$container-border-global-size:          1px !default;
$container-border-global-style:         solid !default;
$container-border-global-color-base:    $color-greyLight !default;
$container-border-global-color-dark:    $color-grey !default;
$container-border-global-base:          $container-border-global-size $container-border-global-style $color-greyLight !default;
$container-border-global-dark:          $container-border-global-size $container-border-global-style $color-grey !default;


// Drop shadows
// -----------------------------------------------------------------------------

$container-drop-shadow-base:             0 2px 0 rgba($color-greyLight, 0.6) !default;


// Backgrounds
// -----------------------------------------------------------------------------

$container-fill-base:                   $color-white !default;
$container-fill-dark:                   $color-greyLight !default;


// Padding
// -----------------------------------------------------------------------------

$container-padding-base:                $spacing-single !default;
$container-padding-large:               $spacing-double !default;


// Margin
// -----------------------------------------------------------------------------

$container-margin-base:                 $spacing-single !default;




// Containers map
// -----------------------------------------------------------------------------

$containersMap: (
    border: (
        base: $container-border-global-base,
        dark: $container-border-global-dark
    ),

    borderColor: (
        base: $container-border-global-color-base,
        dark: $container-border-global-color-dark
    ),

    borderSize: (
        base: $container-border-global-size
    ),

    borderStyle: (
        base: $container-border-global-style
    ),

    dropShadow: (
        base: $container-drop-shadow-base
    ),

    fill: (
        base: $container-fill-base,
        dark: $container-fill-dark
    ),

    margin: (
        base: $container-margin-base
    ),

    padding: (
        base: $container-padding-base,
        large: $container-padding-large
    )
);


// Containers map function
// -----------------------------------------------------------------------------

@function container($property, $variant: "base") {
    @if map-has-key($containersMap, $property) {
        @return map-get(map-get($containersMap, $property), $variant);
    }

    @warn "Unknown `#{$property}` in $containersMap.";
    @return null
}

