ysw/components/accordeon.scss:
.ysw-c-accordion {
    border-bottom: remCalc(1px) solid $ysw-color-accordeon-border;

    &:first-child {
        border-top: remCalc(1px) solid $ysw-color-accordeon-border;
    }

    &:last-child {
        @include ysw-breakpoint-down("medium") {
            border-bottom: 0;
        }
    }

    &__title {
        color: $ysw-color-text-base;
        cursor: pointer;
        display: flex;
        font-size: remCalc(15px);
        font-weight: 700;
        line-height: 1.4;
        list-style: none;
        padding: remCalc(15px 30px 15px 0);
        padding-top: remCalc(18px);
        position: relative;

        &::before,
        &::after {
            background-color: currentColor;
            content: "";
            height: remCalc(1px);
            position: absolute;
            right: 0;
            top: calc(50% - #{remCalc(1px)});
            transition: 0.3s ease;
            transition-property: transform, opacity;
            width: remCalc(11px);
        }

        &::before {
            transform: rotate(-90deg);
        }

        &::after {
            [open]:not(.is-closing) & {
                transform: rotate(180deg);
            }
        }

        &::before {
            [open]:not(.is-closing) & {
                opacity: 0;
                transform: rotate(90deg);
            }
        }
    }

    &__panel {
        overflow: hidden;
        transition: height 0.3s ease;

        &-content {
            opacity: 0;
            transform: translateY((-(#{remCalc(5px)})));
            transition: 0.3s ease-out;
            transition-property: transform, opacity;

            [open]:not(.is-closing) & {
                opacity: 1;
                transform: translateY(0);
            }
        }
    }
}

ysw/components/sticky-product.scss:
.ysw-c-sticky-product-info {
    width: 100%;
    position: fixed;
    z-index: 8;
    bottom: (-(remCalc(200px)));
    transition: all 0.3s ease-in-out;
    opacity: 0;

    &.is-active {
        bottom: 0;
        opacity: 1;
    }

    &__container {
        padding: remCalc(10px 20px 20px);
        background-color: $ysw-color-white;
        box-shadow: $ysw-color-sticky-product-shadow 0 remCalc(8px 24px);
        display: flex;
        align-items: center;
        gap: remCalc(20px);
    }

    &__image {
        margin: 0;
        max-width: remCalc(70px);
        aspect-ratio: 1/1;
        flex: 1;
    }

    &__wrapper {
        display: flex;
        justify-content: space-between;
        flex: 1;
        flex-wrap: wrap;
        gap: remCalc(5px);
    }

    &__description {
        max-width: remCalc(600px);
        padding-top: remCalc(10px);

        &-title {
        font-weight: 600;
        margin-block: remCalc(0 5px);
        font-size: remCalc(14px);

            @include ysw-breakpoint("handheld") {
                font-size: remCalc(16px);
            }
        }
    }

    &__form {
        gap: remCalc(10px 15px);
        flex-wrap: wrap;
        display: flex;
        align-items: center;

        .form-field,
        input {
            margin: 0;
        }
    }
}

components/stencil/search/search.scss:
// =============================================================================
// SEARCH
// =============================================================================

.search-suggestion {
    margin-bottom: spacing("single") + spacing("third");

    > :last-child {
        margin-bottom: 0;
    }
}

.suggestion-title {
    margin-top: 0;
    text-transform: inherit;
}

.advancedSearch-form {
    margin-bottom: spacing("double");

    .form-row {
        margin-bottom: -(spacing("single"));
    }
}

.quickSearchResults {
    margin-top: spacing("single");

    @include breakpoint("medium") {
        margin-top: 0;
    }

    .modal-close {
        display: none;

        @include breakpoint("medium") {
            display: block;
        }
    }
}

.advancedSearch-separator {
    display: none;
}

.advancedSearch-title {
    margin: 0 0 spacing("single");
    text-transform: inherit;
}

.search-price-range {
    label {
        display: inline;

        // scss-lint:disable SelectorDepth, NestingDepth
        input {
            display: block;
            padding-left: spacing("single");

            @include breakpoint("small") {
                display: inline;
                width: 6rem;
            }
        }

        // scss-lint:disable SelectorDepth, NestingDepth
        span {
            @include breakpoint("small") {
                display: inline;
                padding-left: spacing("single");
                padding-right: spacing("single");
            }
        }
    }
}

.category-suggestion-list {
    @include grid-row($behavior: "nest");
    list-style-type: none;
}

.category-suggestion {
    @include grid-column(4);
    font-size: fontSize("smaller");
}

.search-refine {
    margin-left: spacing("single");
}

.quickSearchMessage {
    font-size: fontSize("largest");
    margin: spacing("single") 0 0;
    text-align: center;
}

.search-nav {
    position: relative;
}

ysw/layouts/category/category.scss:
