    //

    // Make sure textarea takes on height automatically
    textarea { height: auto; min-height: 50px; }

    // Make select elements 100% width by default
    select { width: 100%; }
  }
}

// =============================================================================
// COLORS (CSS)
// =============================================================================


// Bigcommerce Utility Classes
// -----------------------------------------------------------------------------
//
// 1. If set to false, there is no outputted CSS for this component.
//
// -----------------------------------------------------------------------------
// scss-lint:disable ImportantRule

@if $exportCSS--colorUtilities { // 1

    @each $color in map-keys($colorMap) {

        @each $variant in map-keys(map-get($colorMap, $color)) {

            .u-bg#{ colorUtilityClassName($color, $variant) } {
                background-color: color($color, $variant) !important;
            }

            .u-color#{ colorUtilityClassName($color, $variant) } {
                color: color($color, $variant) !important;
            }

        }

    }

}

// =============================================================================
// COLORS (Tools)
// =============================================================================

@function colorUtilityClassName($color, $variant) {
    $colorFirstLetter: to-upper-case(str-slice($color, 0, 1));
    $variantFirstLetter: to-upper-case(str-slice($variant, 0, 1));
    $colorStr: $colorFirstLetter + str-slice($color, 2);
    $variantStr: "";

    @if $variant != "base" {
        $variantStr: $variantFirstLetter + str-slice($variant, 2);
    }

    $className: $colorStr + $variantStr;
    @return $className;
}

// =============================================================================
// ALIGN (CSS)
// =============================================================================


// Bigcommerce Utility Classes
// -----------------------------------------------------------------------------
//
// 1. If set to false, there is no outputted CSS for this component.
// Adapted from SUIT CSS: https://github.com/suitcss/utils-align/
//
// -----------------------------------------------------------------------------
// scss-lint:disable ImportantRule

@if $exportCSS--alignUtilities { // 1

    .u-alignBaseline {
        vertical-align: baseline !important;
    }

    .u-alignBottom {
        vertical-align: bottom !important;
    }

    .u-alignMiddle {
        vertical-align: middle !important;
    }

    .u-alignTop {
        vertical-align: top !important;
    }

}

// =============================================================================
// DISPLAY (Tools)
// =============================================================================


@mixin u-display($variant, $important: null) {

    @if $variant == "none" {

        display: none $important;

    }
    @else if $variant == "block" {

        display: block $important;

    }
    @else if $variant == "inlineBlock" {

        // ---------------------------------------------------------------------
        //
        // 1. Fix for Firefox bug: an image styled `max-width:100%` within an
        //    inline-block will display at its default size, and not limit its
        //    width to 100% of an ancestral container.
        //
        // ---------------------------------------------------------------------

        display: inline-block $important;
        max-width: 100% $important; // 1
    }
}

@mixin u-hidden($variant, $important: null) {

    @if $variant == "fully" {

        display: none $important;

    }
    @else if $variant == "visually" {

        border: 0 $important;
        clip: rect(1px, 1px, 1px, 1px) $important;
        height: 1px $important;
        overflow: hidden $important;
        padding: 0 $important;
        position: absolute $important;
        width: 1px $important;
    }
}

// =============================================================================
// DISPLAY (CSS)
// =============================================================================


// Bigcommerce Utility Classes
// -----------------------------------------------------------------------------
//
// 1. If set to false, there is no outputted CSS for this component.
// Adapted from SUIT CSS: https://github.com/suitcss/utils-display/
//
// 2. is-srOnly stands for "is screen reader only", meaning the content is not
// visible to users, but is read out by assistive technology like screen readers.
// Can be used to give greater context about a component when the visual treatment
// is minimal with no supportive text to describe it.
//
// -----------------------------------------------------------------------------

@if $exportCSS--displayUtilities { // 1

    .u-block {
        @include u-display("block", !important);
    }

    .u-inlineBlock {
        @include u-display("inlineBlock", !important);
    }

    .u-hidden {
        @include u-hidden("fully", !important);
    }

    .u-hiddenVisually {
        @include u-hidden("visually", !important);
    }

    .is-srOnly { // 2
        @include u-hidden("visually", !important);
    }

}

// =============================================================================
// LAYOUT (Tools)
// =============================================================================

@mixin u-clearFloats($important: null) {

    &:before,
    &:after {

        // ---------------------------------------------------------------------
        //
        // 1. The space content is one way to avoid an Opera bug when the
        //    'contenteditable' attribute is included anywhere else in the
        //    document. Otherwise it causes space to appear at the top and
        //    bottom of the element.
        // 2. The use of `table` rather than `block` is only necessary if using
        //    ':before' to contain the top-margins of child elements.
        //
        // ---------------------------------------------------------------------

        content: " " $important; // 1
        display: table $important; // 2
    }

    &:after {

        clear: both $important;
    }
}

