        }

        .form-select {
            max-width: none;
        }
    }

}

@mixin formRow--grid() {

    @include grid-row($behavior:nest);

    .form-field {
        @include grid-column(12);
        max-width: none;
    }

    @include breakpoint("small") {

        &--half {
            .form-field {
                width: grid-calc(6, $total-columns);
            }
        }

        &--third {
            .form-field {
                width: grid-calc(4, $total-columns);
            }
        }

        &--quarter {
            .form-field {
                width: grid-calc(3, $total-columns);
            }
        }

    }

}


