        border-bottom: $tab-title-border-bottom-height $tab-title-border-bottom-style $tab-title-border-color;
        display: $tab-display;
        list-style: none;
        margin: $tab-margin;
        padding: $tab-navigation-container-padding;
        position: relative;

        @include breakpoint(small) {
            float: $default-float;
        }

    }

    %tab--active {
        border-bottom-color: $tab-title-active-border-color;
    }

    %tab-title {

        background-color: $tabs-navigation-bg-color;
        color: $tabs-navigation-font-color;
        display: $tab-navigation-display;
        font-family: $tabs-navigation-font-family;
        font-size: $tabs-navigation-font-size;
        padding: $tabs-navigation-padding $tabs-navigation-padding * 2;

        &:hover {
            background-color: $tabs-navigation-hover-bg-color;
            color: $tabs-navigation-active-font-color;
        }

    }

    %tab-title--active {
        background-color: $tabs-navigation-active-bg-color;
        color: $tabs-navigation-active-font-color;
        font-weight: $tab-title-active-font-weight;
    }


    // =============================================================================
    // TABS
    // =============================================================================

    .tabs {
        @extend %tabs;
    }

    .tab {
        @extend %tab;
    }

    .tab.is-active {
        @extend %tab--active !optional;

        .tab-title {
            @extend %tab-title--active;
        }
    }

    .tab-title {
        @extend %tab-title;
    }


    .tabs--vertical {

        @include breakpoint(small) {
            border-bottom: $tabs--vertical-borderBottom;
            float: $tabs--vertical-float;
            margin: 0 0 $tabs-vertical-navigation-margin-bottom;
            max-width: $tabs--vertical-maxWidth;
            width: $tabs--vertical-width;
        }

        .tab {
            float: $tabs--vertical-tab-float;
        }

    }


    // =============================================================================
    // TAB CONTENT
    // =============================================================================

    .tabs-contents {
        @include clearfix;
        margin-bottom: $tabs-content-margin-bottom;
    }

    .tabs-contents--vertical {

        @include breakpoint(small) {

            float: $tabs-contents--vertical-floatDirection;
            max-width: $tabs-contents--vertical-maxWidth;
            padding-#{$tabs-contents--vertical-floatDirection}: $tabs-contents--vertical-floatDirection-padding;
            width: $tabs-contents--vertical-width;

            > .tab-content {
                padding: $tabs-contents--vertical-padding;
            }

        }

    }

    .tab-content {
        display: none;
        padding: $tabs-content-padding;

        &.is-active {
            display: block;
        }
    }

    .no-js {
        .tab-content {
            display: block;
            float: none;
        }
    }
}

// =============================================================================
// NAV (Tools)
// =============================================================================


// Base Object
// -----------------------------------------------------------------------------
//
// This mixin is created by bending our style guides rules.
// To achieve both total control, and the freedom of appending your own
// class name prefix to the elements, the '&' selector has been used heavily.
//
// 1. $style arguments: "bar" or "list", $wrapper arguments: "nav" or "ul".
// 2. Strip basic list styles from all lists placed inside a nav component.
// 3. If your nav a simple <ul> or <ol> element, set $type to "list".
// 4. If your nav is wrapped in a <nav> element, set $type to "block".
// 5. Adds basic visual styling to the actions.
//
// -----------------------------------------------------------------------------

@mixin nav($style: "navBar", $wrapper: "nav") { // 1

    @include clearfix;

    ul,
    ol { // 2
        @include u-listReset;
    }

    @if $wrapper == "ul" { // 3
        @include u-listReset;
    }

    @if $wrapper == "nav" { // 4
        &-section {
            @include clearfix;

            @if $style == "navBar" {
                float: $navBar-item-floatDirection;
            }
        }

        &-section--alt {
            float: $navBar-item-floatOppositeDirection;
            margin-#{$navBar-item-floatDirection}: $navBar-section-margin;
        }
    }

    @if $style == "navList" {
        &-section + &-section {
            margin-top: $navList-section-margin;
        }
    }

    @if $style == "navBar" {
        @if $wrapper == "nav" {
            &-section + &-section {
                margin-left: $navBar-section-margin;
            }
        }

        &-item {
            @include breakpoint("small") {
                float: $navBar-item-floatDirection;
            }
        }
    }

    &-item,
    &-action {
        display: block;
    }

}

@mixin nav-action($color, $fontFamily, $fontSize, $padding) {
    &-action {
        color: $color;
        font-family: $fontFamily;
        font-size: $fontSize;
        padding: $padding;
    }
}

// =============================================================================
// MEDIA Object (Tools)
// =============================================================================

@mixin media($style: "default") { // 1

    @include clearfix;

    @if $style == "default" { // 2
        &-figure {
            float: $default-float;
            margin-#{$opposite-direction}: $media-gutter;

            > img {
                display: block;
            }
        }

        &-figure--opposite {
            float: $opposite-direction;
            margin-#{$default-float}: $media-gutter;
            margin-#{$opposite-direction}: 0;
        }

        &-body {
            @include clearfix;
        }
    }

    @if $style == "table" { // 3
        display: table;
        padding: $mediaTable-gutter;

        &-column {
            display: table-cell;
            padding-right: $mediaTable-gutter;
            vertical-align: $mediaTable-verticalAlignment;

            &:last-child {
                padding: 0;
            }
        }

        &-figure {
            text-align: $mediaTable-image-textAlignment;
            vertical-align: $mediaTable-image-verticalAlignment;
            width: $mediaTable-image-width;
        }

        &-body {
            vertical-align: $mediaTable-body-verticalAlignment;
        }

        &-actions {
            text-align: $mediaTable-actions-textAlign;
            vertical-align: $mediaTable-actions-verticalAlignment;
            width: $mediaTable-actions-width;
        }

    }
}

