    .datepicker-next {
        float: right;
        margin-right: $datepicker-back-next-margin;

        &::before {
            content: $datepicker-next-content;
            display: block;
        }
    }

    .datepicker-days {
        border-collapse: separate;
        color: $datepicker-days-color;
        font-size: $datepicker-days-fontSize;
        margin: $datepicker-days-margin;
