        }
    }

    .card-figcaption-action {
        display: block;
        height: 100%;
    }

    .card-figcaption-body {
        padding-left: $card-figcaption-body-paddingHorizontal;
        padding-right: $card-figcaption-body-paddingHorizontal;
        position: relative;
        top: 50%;
        transform: translateY(-50%);
        transform-style: preserve-3d;

        > :first-child {
            margin-top: 0;
        }

        > :last-child {
            margin-bottom: 0;
        }

