@mixin addFocusTooltip () {
    &:focus {
        &:before {
            content: " ";
            position: absolute;
            right: 0;
            top: 50%;
            border-width: remCalc(10px);
            border-style: solid;
            border-color: transparent transparent $adminBar-tooltip-bg-backgroundColor transparent;
        }

        &:after {
            content: attr(title);
            padding: remCalc(4px) remCalc(6px);
            background-color: $adminBar-tooltip-bg-backgroundColor;
            color: white;
            position: absolute;
            font-size: 1rem;
            white-space: nowrap;
            right: 0;
            top: 100%;
            cursor: default;
            border-radius: remCalc(8px);
        }
    }
}

.modal {
    margin: 0;
    max-height: 90%;
    max-width: 95%;
    min-height: 240px;
    outline: none;
    overflow: hidden;
    padding: 0;
    left: 50%;
    top: 50% !important;
    transform: translate(-50%, -50%);

