    }
}

components/stencil/paymentMethods/paymen:
// =============================================================================
// PAYMENT METHODS (CSS)
// =============================================================================

.paymentMethodsTitle {
    padding: 0;
    text-transform: none;
    width: 100%;
    margin: 0 0 spacing("half");
}

.paymentMethodsGrid {
    margin: -(spacing("half"));
    margin-bottom: spacing("single");
    display: flex;
    flex-wrap: wrap;

    &-item {
        padding: spacing("half");;
        width: 100%;

        @include breakpoint("small") {
            width: grid-calc(6, $total-columns);
        }
    }
}

.paymentMethod {
    border: container("border");
    background-color: #e5e5e5;
    border-radius: remCalc(3px);
    height: 100%;
    display: flex;
    flex-flow: column nowrap;

    &-row {
        padding: 1.5rem;

        &:first-child {
            background-color: #ffffff;
        }

        &:last-child {
            margin-top: auto;
        }

        &:only-child {
            height: 100%;
        }
    }

    // fix
    .button {
        margin-bottom: 0;
    }
}

.methodHeader {
    justify-content: space-between;
    display: flex;
    flex-flow: row;
    align-items: center;

    &-icon {
        margin-right: spacing("half");
        width: remCalc(28px);

        @include breakpoint("large") {
            width: remCalc(42px);
        }
    }

    &-title {
        font-size: fontSize("smallest");
        font-weight: fontWeight("semibold");

        @include breakpoint("large") {
            font-size: fontSize("base");
        }
    }

    &-brand {
        font-size: fontSize("smallest");
        font-weight: fontWeight("semibold");

        @include breakpoint("large") {
            font-size: fontSize("base");
        }
    }

    &-meta {
        padding-left: spacing("half");
        margin-left: auto;
        display: flex;
        flex-wrap: nowrap;
    }

    &-default {
        margin-left: spacing("half");
        fill: color("greys", "dark");
        height: remCalc(18px);
        width: remCalc(18px);

        @include breakpoint("large") {
            height: remCalc(20px);
            width: remCalc(20px);
        }
    }

    &-expiry {
        font-size: fontSize("smaller");
        text-transform: capitalize;
    }
}

.methodDetails {
    margin: 0;
    display: flex;

    &-label {
        flex: 1;
        font-weight: fontWeight("semibold");
    }

    &-description {
        flex: 2;
        font-size: fontSize("smallest");
    }
}

.newPaymentMethod {
    text-decoration: none;
    display: flex;
    align-items: center;
    justify-items: center;
    flex-direction: column;
    min-height: remCalc(186px);
    height: 100%;

    &-icon {
        font-size: 50px;
        line-height: 1;
        margin: auto 0 spacing("half");
    }

    &-title {
        margin: 0 auto auto;
        text-align: center;
    }
}

// Payment Method Form - Add
// -----------------------------------------------------------------------------

.paymentMethodForm {
    @include breakpoint("small") {
        max-width: 60%;
    }

    &-column {
        @include breakpoint("small") {
            display: flex;

            &:first-child .form-field {
                margin-bottom: spacing("half");
            }
        }
    }

    &-heading {
        color: color("greys", "darker");
        font-family: fontFamily("sans");
        margin-top: spacing("single");
        text-transform: capitalize;
    }

    &-subheading {
        display: flex;
        font-weight: fontWeight("semibold");
    }

    &-cards {
        @include breakpoint("small") {
            margin-left: auto;
        }
    }

    &-cards-icon {
        margin-right: spacing("eighth");
        width: remCalc(34px);
    }

    &-inputs {
        position: relative;

        @include breakpoint("small") {
            &:last-child {
                padding-left: spacing("single");
                width: 30%;
            }

            &:first-child {
                width: 70%;
            }
        }
    }

    &-inputs-icon {
        position: absolute;
        right: remCalc(9px);
        top: remCalc(39px);

        &.icon {
            fill: color("greys", "light");
            height: remCalc(21px);
            width: remCalc(21px);
        }
    }

    &-details {
        margin-bottom: 0;

        @include breakpoint("small") {
            display: inline-block;
            margin: 0 spacing("double") 0 0;
        }
    }

    &-details-term {
        font-weight: fontWeight("normal");
        margin-bottom: spacing("half");
    }

    &-details-description {
        font-size: fontSize("smaller");
        font-weight: fontWeight("semibold");
        letter-spacing: letterSpacing("large");
    }
}

layouts/epicContent/fontawesome/core.scs:
// Base Class Definition
// -------------------------

.#{$fa-css-prefix},
.fas,
.far,
.fal,
.fad,
.fab {
  -moz-osx-font-smoothing: grayscale;
  -webkit-font-smoothing: antialiased;
  display: inline-block;
  font-style: normal;
  font-variant: normal;
  text-rendering: auto;
  line-height: 1;
}

%fa-icon {
  @include fa-icon;
}

layouts/epicContent/fontawesome/mixins.s:
// Mixins
// --------------------------

@mixin fa-icon {
  -webkit-font-smoothing: antialiased;
  -moz-osx-font-smoothing: grayscale;
  display: inline-block;
  font-style: normal;
  font-variant: normal;
  font-weight: normal;
  line-height: 1;
}

@mixin fa-icon-rotate($degrees, $rotation) {
  -ms-filter: "progid:DXImageTransform.Microsoft.BasicImage(rotation=#{$rotation})";
  transform: rotate($degrees);
}

@mixin fa-icon-flip($horiz, $vert, $rotation) {
  -ms-filter: "progid:DXImageTransform.Microsoft.BasicImage(rotation=#{$rotation}, mirror=1)";
  transform: scale($horiz, $vert);
}


// Only display content to screen readers. A la Bootstrap 4.
//
// See: http://a11yproject.com/posts/how-to-hide-content/

@mixin sr-only {
  border: 0;
  clip: rect(0, 0, 0, 0);
  height: 1px;
  margin: -1px;
  overflow: hidden;
  padding: 0;
  position: absolute;
  width: 1px;
}

// Use in conjunction with .sr-only to only display content when it's focused.
//
// Useful for "Skip to main content" links; see http://www.w3.org/TR/2013/NOTE-WCAG20-TECHS-20130905/G1
//
// Credit: HTML5 Boilerplate

@mixin sr-only-focusable {
  &:active,
  &:focus {
    clip: auto;
    height: auto;
    margin: 0;
    overflow: visible;
    position: static;
    width: auto;
  }
}

components/vendor/nanobar/nanobar.scss:
// =============================================================================
// NANOBAR - AJAX LOADING PROGRESS
// =============================================================================

.nanobar {
    display: none;
    width: 100%;
    height: remCalc(5px);
    z-index: 9999;
    top: 0;
    pointer-events: none;
    user-select: none;

    .bar {
        width: 0;
        height: 100%;
        transition: height .1s;
        background-color: stencilColor("pace-progress-backgroundColor");
    }
}

layouts/epicContent/fontawesome/larger.s:
// Icon Sizes
// -------------------------

// makes the font 33% larger relative to the icon container
.#{$fa-css-prefix}-lg {
  font-size: (4em / 3);
  line-height: (3em / 4);
  vertical-align: -.0667em;
}

.#{$fa-css-prefix}-xs {
  font-size: .75em;
}

.#{$fa-css-prefix}-sm {
  font-size: .875em;
}

@for $i from 1 through 10 {
  .#{$fa-css-prefix}-#{$i}x {
    font-size: $i * 1em;
  }
}

layouts/epicContent/fontawesome/fixed-wi:
// Fixed Width Icons
// -------------------------
.#{$fa-css-prefix}-fw {
  text-align: center;
  width: $fa-fw-width;
}

layouts/epicContent/fontawesome/list.scs:
// List Icons
// -------------------------

.#{$fa-css-prefix}-ul {
  list-style-type: none;
  margin-left: $fa-li-width * 5/4;
  padding-left: 0;

  > li { position: relative; }
}

.#{$fa-css-prefix}-li {
  left: -$fa-li-width;
  position: absolute;
  text-align: center;
  width: $fa-li-width;
  line-height: inherit;
}

