        width: 100%;
        z-index: 89;
        max-width: 200px;
        &.is-open
        {
            display:block;
        }
    }
    &.is-open
    {
        display:block;
        left:0!important;
    }
    &:before
    {
        border: inset 10px;
        content: "";
        display: block;
        height: 0;
        width: 0;
        border-color: transparent transparent #FFFFFF transparent;
        border-bottom-style: solid;
        position: absolute;
        top: -20px;
        left: 10px;
        z-index: 89;
    }
    &:after
    {
        border: inset 11px;
        content: "";
        display: block;
        height: 0;
        width: 0;
        border-color: transparent transparent #cccccc transparent;
        border-bottom-style: solid;
        position: absolute;
        top: -22px;
        left: 9px;
        z-index: 88;
    }
}
.overlay-shop 
{
    position: fixed;
    padding: 80px 0;
    z-index: 99999;
    overflow: auto;
    width: 100%;
    height: 100%;
    top: 0;
    left: 0;
    background: $shop-info-bg-color;
    color: $shop-info-text-color;
    visibility: hidden;
    backface-visibility: hidden;
    transform: translateX(-100%);
    transition: visibility 0s .5s,-webkit-transform .5s;
    transition: transform .5s,visibility 0s .5s;
    transition: transform .5s,visibility 0s .5s,-webkit-transform .5s;

    .overlay-close 
    {
        width: 30px;
        height: 30px;
        position: absolute;
        right: 20px;
        top: 20px;
        overflow: hidden;
        border: none;
        background-image: url(../img/cross.png);
        background-size: 30px;
        background-position: center center;
        background-repeat: no-repeat;
        text-indent: 200%;
        color: transparent;
        outline: none;
        z-index: 100;
    }
    .container 
    {
        max-width: 1600px;
        padding: 0 35px;
    }
    .col-title 
    {
        color: $shop-info-heading-color;
        margin-top: 0;
    }
    ul 
    {
        margin: 0;
        list-style: none;
        li 
        {
            display: block;
            font-size: 1rem;
            line-height: 1.5;
            a
            {
                color: $shop-info-text-color;
            }
        }
    }
    .info-col-3 
    {
        border-left: 1px solid $shop-info-border-color;
        img
        {
            float: left;
            margin: 0 15px 5px 0;
        }
        p
        {
            font-size: 1.3em;
        }
    }
}

.top-bar 
{
    background: $top-bar-bg;
    font-family:$topbar-font-family;
    height: auto;
    padding: 0;
    overflow: visible;
    z-index: 999;
    a:not(.button)
    {
        color: $top-bar-text;
        &:hover
        {
            color: $top-bar-text-hover;
        }
    }
    .top-google-translate
    {
        display: inline-block;
        vertical-align: middle;
        margin-right: 10px;
        .goog-te-gadget-simple 
        {
            background:none!important;
            border: none!important;
            height: 44px;
            line-height: 44px;
            .goog-te-menu-value
            {
                color: $top-bar-text;
                span
                {
                    color: $top-bar-text!important;
                    border-color:$top-bar-text!important; 
                    font-family:$topbar-font-family;
                    font-size: $top-bar-fontSize-root;
                }
            }
        }
    }
    .top-currency-selector
    {
        display: inline-block;
        vertical-align: middle;
        ul 
        {
            margin-left: 0;
            margin-bottom:0;
            .navUser-action
            {
                text-transform: none;
                font-size: $top-bar-fontSize-root;
                color: white;
                font-weight: normal;
                padding: 0.822rem 0.78571rem;
                svg
                {
                    fill: $top-bar-text;
                    stroke: $top-bar-text;
                }
                &:hover, &.is-open
                {
                    color: $top-bar-text;
                    svg
                    {
                        fill: $top-bar-text;
                        stroke: $top-bar-text;
                    }
                }
            }
            .dropdown-menu 
            {
                background: #ffffff;
                border: 1px solid #6b90da;
                padding: 4px;
                box-shadow: 0 3px 8px 2px #999999;
                max-width: 160px;
                .dropdown-menu-item
                {
                    line-height: 1;
                    padding: 0;
                    a
                    {
                        color: #0000cc;
                        font-family: arial;
                        padding: 4px;
