layouts/epicContent/epic-content/content:
.body.home 
{
    margin:0;
}
.main-wrapper 
{
    overflow-x: visible;
    -webkit-transition: -webkit-transform 0.5s;
    transition: transform 0.5s;
}

.hs-clean .wrapper-contentpush
{
    -webkit-transform: translateX(50%);
    transform: translateX(50%);
    height:100vh;
    overflow:hidden;
}
.main-wrapper:after
{
    content: '';
    opacity: 0;
    visibility: hidden;
    position: absolute;
    top: 0;
    left: 0;
    width: 100%;
    height: 100%;
    background: rgba(0,0,0,0.6);
    -webkit-transition: opacity 0.5s, visibility 0s 0.5s;
    transition: opacity 0.5s, visibility 0s 0.5s;
}

.main-wrapper.wrapper-contentpush:after 
{
    visibility: visible;
    opacity: 1;
    -webkit-transition: opacity 0.5s;
    transition: opacity 0.5s;
}
.slick-next, .slick-prev
{
    background-color: transparent;
    border: none;
    &:hover, &:focus
    {
        background-color: transparent;
        border: none;
    }
}
.heroCarousel
{
    margin:0!important;
    .slick-dots 
    {
        text-align: right;
        padding-right: 15px;
        li
        {
            background-color: transparent;
            height: 2rem;
            width: 2rem;
            margin: 0;
            padding: 0;
            button
            {
                border:none;
                &:before
                {
                    color:$epic-slick-dot-color;
                    background-color:$epic-slick-dot-color;
                    opacity:1;
                }
            }
            &.slick-active
            {
                button:before
                {
                    color:$epic-slick-dot-color-active;
                    background-color:$epic-slick-dot-color-active;
                }
            }
        }
    }
    .slick-prev:before
    {
        background-image:$epic-slick-arrow-prev;
    }
    .slick-next:before
    {
        background-image:$epic-slick-arrow-next;
    }
    &.heroCarousel-overlay
    {
        .heroCarousel-slide
        {
            &:before
            {
                position: absolute;
                content: "";
                background: rgba($carousel-bgColor, 0.4);
                top: 0;
                height: 100%;
                left: 0;
                width: 100%;
            }
        }
    }
    @media only screen and (min-width:801px)
