			.btn-wishlist, .quickview, .btn-compare
			{
				transition: all 0.2s cubic-bezier(0.25, 0.46, 0.45, 0.94);
				display: inline-block;
			    background: $card-figcaption-button-background;
			    color: $card-figcaption-button-color;
			    border-radius: 0;
			    border: 1px solid $card-figcaption-button-color;
			    font-size: 0;
			    padding: 0;
			    margin:10px 3px;
			    line-height: 40px;
			    height: 40px;
			    width: 40px;
			    position: relative;
			    [class^="fa"]
			    {
			    	font-size: 14px;
			    	vertical-align: middle;
			    }
			    &:after
			    {
			    	width: 100%;
				    content: "";
				    position: absolute;
				    z-index: 0;
				    transition: all 0.4s cubic-bezier(0.25, 0.46, 0.45, 0.94);
				    top: 0;
				    left: 0;
				    bottom: 0;
				    right: 0;
				    margin: 0 auto;
				    height: 100%;
				    background: 0 0;
			    }
			    &:hover
			    {
			    	color: $card-figcaption-button-background;
    				background-color: $card-figcaption-button-color;
    				&:after
    				{
    					width: 0;
    					background-color: $card-figcaption-button-background;
    				}
			    }
			}
			.box-compare
			{
				display: inline-block;
				input[type="checkbox"]
				{
					position: absolute;
					opacity: 0;
					&:checked + [class^="fa"]:before
					{
						content: "\f14a";
					}
				}
			}
			.box-cart
			{
				.card-figcaption-button
				{
					transition: all 0.2s cubic-bezier(0.25, 0.46, 0.45, 0.94);
					display: inline-block;
				    background: $card-figcaption-button-background;
				    color: $card-figcaption-button-color;
				    border-radius: 0;
				    border: 1px solid $card-figcaption-button-color;
				    padding: 12px;
				    min-width:140px;
    				font-size: 14px;
    				margin:0;
    				position: relative;
    				[class^="fa"]
				    {
				    	font-size: 16px;
				    	margin-right:5px;
				    }
				    &:after
				    {
				    	width: 100%;
					    content: "";
					    position: absolute;
					    z-index: 0;
					    transition: all 0.4s cubic-bezier(0.25, 0.46, 0.45, 0.94);
					    top: 0;
					    left: 0;
					    bottom: 0;
					    right: 0;
					    margin: 0 auto;
					    height: 100%;
					    background: 0 0;
				    }
				    &:hover
				    {
				    	color: $card-figcaption-button-background;
	    				background-color: $card-figcaption-button-color;
	    				&:after
	    				{
	    					width: 0;
	    					background-color: $card-figcaption-button-background;
	    				}
				    }
				}
			}
		}
	}
	.card-body 
	{
    	text-align: center;
    	.card-title
    	{
    		font-size:16px;
    	}
    	.price-section
    	{
    		font-weight:700;
    	}
	}
}
