    {
        border:none;
        .tab
        {
            border:none;
            background:$buttonStyle-primary-backgroundColor;
            margin:0 5px;
            width:30%;
            float:left;
            &:first-child
            {
                margin-left:0;
                border-top-left-radius: 4px;
                border-bottom-left-radius: 4px;
                &:before
                {
                    border:none;
                }
            }
            &:last-child
            {
                border-top-right-radius: 4px;
                border-bottom-right-radius: 4px;
                &:after
                {
                    border:none;
                }
            }
            &:after, &:before
            {
                content: " ";
                position: absolute;
                top: 0;
                right: -23px;
