layouts/epicContent/epic-content/home-sn:
.snippetContainer
{
    -webkit-perspective: 1000px;
    -moz-perspective: 1000px;
    -ms-perspective: 1000px;
    perspective: 1000px;
    -ms-transform: perspective(1000px);
    -moz-transform: perspective(1000px);
    -moz-transform-style: preserve-3d; 
    -ms-transform-style: preserve-3d; 
    .snippet-content 
    {
        position: relative;
        min-height: 260px;
        .snippet-content-img
        {
            position: absolute;
            height: 100%;
            width: 100%;
            left: 0;
            top: 0;
            background-size: cover;
            background-position: center center;
        }
        .snippet-content-figure
        {
            min-height: 260px;
            position: relative;
            .snippet-content-wrap
            {
                padding: 30px 20px;
                text-align:center;
                position: absolute;
                left: 0;
                width: 100%;
                top: 50%;
                -webkit-transform: translateY(-50%);
                -moz-transform: translateY(-50%);
                transform: translateY(-50%);
                .small
                {
                    font-size: 24px;
                    @media screen and (min-width:1401px)
                    {
                        font-size:36px;
                    }
                }
                .medium
                {
                    font-size: 36px;
                    @media screen and (min-width:1401px)
                    {
                        font-size:60px;
                    }
                }
                .large
                {
                    font-size: 48px;
                    @media screen and (min-width:1401px)
                    {
                        font-size:72px;
                    }
                }
                .extra-large
                {
                    font-size: 60px;
                    @media screen and (min-width:1401px)
                    {
                        font-size:100px;
                    }
                }
                .snippet-title
                {
                    margin: 0 0 5px;
                    font-size: 18px;
                    text-transform: uppercase;
                    font-weight: bold;
                }
                .snippet-des
                {
                    margin: 0;
                    line-height: 20px;
                }
                .snippet-btn
                {
                    margin: 15px 0 0;
                    a
                    {
                        display: inline-block;
                        padding: 10px 15px;
                        font-size: 14px;
                        line-height: 1;
                        border-radius: 3px;
                    }
                }
                &.snippet-icon-top
                {
                    [class^="fa"]
                    {
                        margin-bottom:15px;
                    }
                }
                &.snippet-icon-right
                {
                    text-align:left;
                    padding-right: 80px;
                    @media screen and (min-width:1401px)
                    {
                        width:100%;
                        right:0;
                        padding-right:50%;
                        left:0;
                        display:table;
                        margin:0 auto;
                    }  

                    [class^="fa"]
                    {
                        position: absolute;
                        right: 0;
                        width: 80px;
                        @media screen and (min-width:1401px)
                        {
                            width:50%;
                        }
                        text-align: center;
                        top: 50%;
                        -webkit-transform: translateY(-50%);
                        -moz-transform: translateY(-50%);
                        transform: translateY(-50%);
                        @media screen and (min-width:1401px){
                        width:50%;
                        text-align:center;
                        }
                    }
                }
                &.snippet-icon-bottom
                {
                    [class^="fa"]
                    {
                        margin-top:15px;
                    }
                }
                &.snippet-icon-left
                {
                    text-align:left;
                    padding-left: 80px; 
                    @media screen and (min-width:1401px)
                    {
                        width: 100%;
                        left: 0;
                        padding-left:50%;
                    }  

                    [class^="fa"]
                    {
                        position: absolute;
                        left: 0;
                        width: 80px;
                        @media screen and (min-width:1401px)
                        {
                            width:50%;
                        }
                        text-align: center;
                        top: 50%;
                        -webkit-transform: translateY(-50%);
                        -moz-transform: translateY(-50%);
                        transform: translateY(-50%);
                    }

                }
            }
        }
        &.snippet-flip-n
        {
            .snippet-content-img:after
            {
                content: "";
                background: rgba(0, 0, 0, 0.6);
                top: 0;
                left: 0;
                height: 100%;
                width: 100%;
                position: absolute;
            }
            .snippet-content-figure
            {
                position:relative;
                z-index:1;
                background:none!important;
            }
        }
        &.snippet-flip-h
        {
            -webkit-transition: 0.6s;
            -webkit-transform-style: preserve-3d;
            -ms-transition: 0.6s;
            -moz-transition: 0.6s;
            -moz-transform: perspective(1000px);
            -moz-transform-style: preserve-3d;
