		    .ch-item
		    {
		    	width:100%;
		    	height:auto;
		    	.ch-info
		    	{
		    		position: static;
		    		display: -webkit-box;
			      	display: -ms-flexbox;
			      	display: flex;
				      -ms-flex-wrap: wrap;
				      flex-wrap: wrap;
		    		> div
		    		{
		    			position: static;
					    height: auto;
					    border-radius: 0;
					    box-shadow: none;
					    -webkit-box-flex: 0;
				        -ms-flex: 0 0 50%;
				        flex: 0 0 50%;
