components/epicNavbar/default-nav-item:
{{#if children}}
<a class="navPages-action has-subMenu" href="{{url}}" data-collapsible="navPages-{{id}}">{{name}} <i class="icon navPages-action-moreIcon" aria-hidden="true"><svg><use xlink:href="#icon-chevron-down" /></svg></i>
</a>
<div class="navPage-subMenu" id="navPages-{{id}}" aria-hidden="true" tabindex="-1">
    <div class="container">
        <div class="row">
            <div class="col-12 submenu-column {{#if theme_settings.nav-category-one-name '=='name}}{{#if theme_settings.nav-category-right-image-one}}col-lg-8{{/if}}{{/if}} {{#if theme_settings.nav-category-two-name '=='name}}{{#if theme_settings.nav-category-right-image-two}}col-lg-8{{/if}}{{/if}} {{#if theme_settings.nav-category-three-name '=='name}}{{#if theme_settings.nav-category-right-image-three}}col-lg-8{{/if}}{{/if}} {{#if theme_settings.nav-category-four-name '=='name}}{{#if theme_settings.nav-category-right-image-four}}col-lg-8{{/if}}{{/if}} {{#if theme_settings.nav-category-five-name '=='name}}{{#if theme_settings.nav-category-right-image-five}}col-lg-8{{/if}}{{/if}} {{#if theme_settings.nav-category-six-name '=='name}}{{#if theme_settings.nav-category-right-image-six}}col-lg-8{{/if}}{{/if}} {{#if theme_settings.nav-category-seven-name '=='name}}{{#if theme_settings.nav-category-right-image-seven}}col-lg-8{{/if}}{{/if}}">
                <ul class="navPage-subMenu-list">
                    <li class="navPage-subMenu-item">
                        <a class="navPage-subMenu-action navPages-action" href="{{url}}">{{lang 'category.view_all.name' category=name}}</a>
                    </li>
                    {{#each children}}
                        <li class="navPage-subMenu-item">
                            {{#if children}}
                                <a
                                    class="navPage-subMenu-action navPages-action has-subMenu"
                                    href="{{url}}"
                                    data-collapsible="navPages-{{id}}"
                                    data-collapsible-disabled-breakpoint="medium"
                                    data-collapsible-disabled-state="open"
                                    data-collapsible-enabled-state="closed">
                                    {{name}}<i class="icon navPages-action-moreIcon" aria-hidden="true"><svg><use xlink:href="#icon-chevron-down" /></svg></i>
                                </a>
                                <ul class="navPage-childList" id="navPages-{{id}}">
                                    {{#each children}}
                                    <li class="navPage-childList-item">
                                        {{#if children}}
                                            <a class="navPage-childList-action navPages-action" href="{{url}}">{{name}}</a>
                                            <ul class="navPage-childList" id="navPages-{{id}}">
                                                {{#each children}}
                                                <li class="navPage-childList-item">
                                                    <a class="navPage-childList-action navPages-action" href="{{url}}">{{name}}</a>
                                                </li>
                                                {{/each}}
                                            </ul>
                                        {{else}}
                                            <a class="navPage-childList-action navPages-action" href="{{url}}">{{name}}</a>
                                        {{/if}}
                                    </li>
                                    {{/each}}
                                </ul>
                            {{else}}
                                <a class="as navPage-subMenu-action navPages-action" href="{{url}}">{{name}}</a>
                            {{/if}}
                        </li>
                    {{/each}}
                </ul>
            </div>
            {{#if theme_settings.nav-category-one-name '=='name}}
                {{#if theme_settings.nav-category-right-image-one}}
                    <div class="col-12 col-lg-4 submenu-img-column">
                        <a href="{{theme_settings.nav-category-right-url-one}}">
                            <img src="{{getImage theme_settings.nav-category-right-image-one}}">
                        </a>
                    </div>
                {{/if}}
                {{#if theme_settings.nav-category-bottom-image-one}}
                    <div class="col-12 col-lg-12 submenu-img-bottom">
                        <a href="{{theme_settings.nav-category-bottom-url-one}}">
                            <img src="{{getImage theme_settings.nav-category-bottom-image-one}}">
                        </a>
                    </div>
                {{/if}}
            {{/if}}
            {{#if theme_settings.nav-category-two-name '=='name}}
