<style>
/*--- Trust Images Section ---*/
.trust-section {
	-webkit-box-shadow: 0px 2px 5px 1px rgba(227,227,227,1);
	-moz-box-shadow: 0px 2px 5px 1px rgba(227,227,227,1);
	box-shadow: 0px 0px 5px 1px rgb(242, 242, 242);
}
.trust-items {
	display: flex;
	justify-content: center;
	align-items: center;
	gap: 20px;
	padding: 20px 0;
	
}

/*--- Footer menu ---*/
.footer-items {
  display: grid;
  grid-template-columns: repeat(10 , 1fr);
  margin: 0;
  gap: 5px;
}
.footer-item:first-child {
  grid-column: 1 /span 4;
  padding-left: 0;
  padding-right: 30px;
}
.footer-item {
  grid-column: auto / span 2;
  padding: 0 15px;
}

/*--- Subscription Section ---*/

.newsletter-box {
  display: flex;
  justify-content: space-between;
  align-items: center;
  height: 100%;
}
.subscription-section {
  width: 100%;
  max-width: 450px;
}
.subcription-style {
  justify-content: space-between;
  flex-wrap: nowrap !important;
  border-bottom: 1px solid;
  padding: 0 15px;
}
.subcription-style input {
	margin: 0 !important;
	background: transparent !important;
	color: #000 !important;
	font-size: 15px !important;
	transition: .2s;
	border: none;
	padding: 0 !important;
}
.subcription-style input[type="submit"] {
	padding-left: 15px !important;
}
.subcription-style input:hover[type="submit"] {
	color: #488bc1 !important;
}

.footer-payment {
	display: flex;
	height: 100%;
	align-items: end;
}
.footer-payment-icon li {
  font-size: 26px !important;
}


/*--- bottom foooter ---*/

.copyright-text {
  display: flex;
  justify-content: space-between;
  align-items: center;
}
.copyright-text i {
	font-size: 18px;
	margin-right: 5px;
}
@media (max-width: 810px) {
    /*--- Footer menu ---*/
    .footer-item:first-child,
    .footer-item {
      grid-column: auto / span 5;
      padding: 0 15px;
    }

    /*--- Newsletter Box ---*/
    .newsletter-box {
	    flex-direction: column;
	    gap: 15px;
    }
    
    /*--- Copyright Section ---*/
    .copyright-text {
      flex-direction: column;
    }
}


@media (max-width: 510px) {
    /*--- Trust Images Section ---*/
    .trust-items {
	    gap: 5px;
    }


    /*--- Footer menu ---*/
    .footer-item:first-child,
    .footer-item {
      grid-column: auto / span 10;
    }
    
    /*--- Subscription Section ---*/
    .main-footer .footer-payment-icon li {
	font-size: 22px;
}
}

</style>



